import React, { useState, useCallback } from 'react'
import { useDropzone } from 'react-dropzone'
import { useRouter } from 'next/router'
import { Button } from "@/components/ui/button" // Import de votre bouton personnalisé
import { X, Upload } from 'lucide-react' // Import des icônes

export default function UploadImageForm() {
  const [image, setImage] = useState<string | null>(null) // État pour stocker l'image uploadée
  const router = useRouter()
  // Callback pour gérer le drop d'image
  const onDrop = useCallback((acceptedFiles) => {
    const file = acceptedFiles[0]
    const reader = new FileReader()

    // Quand le fichier est chargé
    reader.onload = (e) => {
      setImage(e.target.result)
    }

    reader.readAsDataURL(file)
  }, [setImage])

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    accept: {
      'image/*': ['.jpeg', '.jpg', '.png', '.gif'] // Formats d'images autorisés
    },
    multiple: false // N'accepter qu'un seul fichier à la fois
  })

  const handleRemove = () => {
    setImage(null) // Remet l'image à null quand on la supprime
  }

  const handleSubmit = () => {
    // Logique pour uploader l'image sur le serveur (à implémenter)
    console.log('Image submitted:', image)
    // Redirection vers une autre page après l'upload
    router.push('/dashboard')
  }

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-50">
      <div className="max-w-md w-full space-y-8 bg-white p-10 rounded-lg shadow-md">
        <div className="text-center">
          <h2 className="mt-6 text-3xl font-extrabold text-gray-900">Profile Picture</h2>
          <p className="mt-2 text-sm text-gray-600">
            Please upload a profile picture to complete your account setup.
          </p>
        </div>

        <div className="mt-8 space-y-6">
          <div className="flex flex-col items-center">
            {/* Affichage de l'image téléchargée */}
            <div className="w-32 h-32 rounded-full overflow-hidden bg-gray-200 flex items-center justify-center mb-4">
              {image ? (
                <img src={image} alt="Profile" className="w-full h-full object-cover" />
              ) : (
                <Upload className="h-12 w-12 text-gray-400" />
              )}
            </div>

            {/* Zone de drop */}
            <div {...getRootProps()} className="w-full">
              <input {...getInputProps()} />
              <div className="mt-1 flex justify-center px-6 pt-5 pb-6 border-2 border-gray-300 border-dashed rounded-md cursor-pointer hover:border-gray-400 transition-colors duration-200">
                <div className="space-y-1 text-center">
                  <Upload className="mx-auto h-12 w-12 text-gray-400" />
                  <div className="flex text-sm text-gray-600">
                    <label htmlFor="file-upload" className="relative cursor-pointer rounded-md font-medium text-indigo-600 hover:text-indigo-500 focus-within:outline-none focus-within:ring-2 focus-within:ring-offset-2 focus-within:ring-indigo-500">
                      <span>{isDragActive ? 'Drop the file here' : 'Upload a file'}</span>
                    </label>
                    <p className="pl-1">or drag and drop</p>
                  </div>
                  <p className="text-xs text-gray-500">PNG, JPG, GIF up to 10MB</p>
                </div>
              </div>
            </div>

            {/* Bouton de suppression */}
            {image && (
              <Button
                onClick={handleRemove}
                className="mt-2 inline-flex items-center px-2.5 py-1.5 border border-transparent text-xs font-medium rounded text-red-700 bg-red-100 hover:bg-red-200 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-red-500"
              >
                <X className="h-4 w-4 mr-1" /> Remove
              </Button>
            )}
          </div>

          {/* Bouton de validation */}
          <Button
            onClick={handleSubmit}
            disabled={!image}
            className="w-full flex justify-center py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 disabled:opacity-50 disabled:cursor-not-allowed"
          >
            Validate
          </Button>
        </div>
      </div>
    </div>
  )
}
