// frontend/src/components/organismes/SignUpForm.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './SignUpForm.css';

const SignUpForm = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    prenom: '',
    nom: '',
    email: '',
    motDePasse: '',
    confirmationMotDePasse: ''
  });
  const [erreurs, setErreurs] = useState({});
  const [erreurSubmit, setErreurSubmit] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));

    // Validation en temps réel pour prénom et nom
    if (name === 'prenom' || name === 'nom') {
      if (!/^[a-zA-ZÀ-ÿ\s'-]+$/.test(value)) {
        setErreurs(prevErrors => ({
          ...prevErrors,
          [name]: 'Ce champ ne doit contenir que des lettres, des espaces, des tirets ou des apostrophes.'
        }));
      } else {
        setErreurs(prevErrors => {
          const newErrors = { ...prevErrors };
          delete newErrors[name];
          return newErrors;
        });
      }
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Vérification finale avant soumission
    if (Object.keys(erreurs).length > 0) {
      setErreurSubmit('Veuillez corriger les erreurs avant de soumettre le formulaire.');
      return;
    }
    // ... le reste de votre logique de soumission
  };

  return (
    <form onSubmit={handleSubmit} className="signup-form">
      <div>
        <label htmlFor="prenom">Prénom:</label>
        <input
          type="text"
          id="prenom"
          name="prenom"
          value={formData.prenom}
          onChange={handleChange}
          required
        />
        {erreurs.prenom && <span className="error">{erreurs.prenom}</span>}
      </div>
      <div>
        <label htmlFor="nom">Nom:</label>
        <input
          type="text"
          id="nom"
          name="nom"
          value={formData.nom}
          onChange={handleChange}
          required
        />
        {erreurs.nom && <span className="error">{erreurs.nom}</span>}
      </div>
      {/* ... autres champs du formulaire ... */}
      {erreurSubmit && <div className="error">{erreurSubmit}</div>}
      <button type="submit">S'inscrire</button>
    </form>
  );
};

export default SignUpForm;
