// frontend/src/components/organismes/SignUpForm.js
import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './SignUpForm.css';

const SignUpForm = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    password: '',
    confirmPassword: '',
  });
  const [agreeTerms, setAgreeTerms] = useState(false);
  const [passwordError, setPasswordError] = useState('');
  const [isFormValid, setIsFormValid] = useState(false);

  useEffect(() => {
    // Vérifier si le formulaire est valide
    const isValid = 
      formData.firstName.trim() !== '' &&
      formData.lastName.trim() !== '' &&
      formData.email.trim() !== '' &&
      formData.password.trim() !== '' &&
      formData.confirmPassword.trim() !== '' &&
      formData.password === formData.confirmPassword &&
      agreeTerms &&
      !passwordError;

    setIsFormValid(isValid);
  }, [formData, agreeTerms, passwordError]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));

    if (name === 'confirmPassword' || name === 'password') {
      if (name === 'confirmPassword' && formData.password !== value) {
        setPasswordError('Les mots de passe ne correspondent pas');
      } else if (name === 'password' && formData.confirmPassword !== '' && formData.confirmPassword !== value) {
        setPasswordError('Les mots de passe ne correspondent pas');
      } else {
        setPasswordError('');
      }
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (isFormValid) {
      try {
        const response = await axios.post('http://localhost:5000/api/auth/signup', {
          email: formData.email,
          password: formData.password,
          firstName: formData.firstName,
          lastName: formData.lastName
        });
        console.log('Inscription réussie', response.data);
        // Rediriger vers la page d'upload d'image
        navigate('/signup-upload-image');
      } catch (error) {
        console.error('Erreur lors de l\'inscription', error.response.data);
        // Gérer l'erreur (par exemple, afficher un message à l'utilisateur)
      }
    }
  };

  return (
    <div className="signup-form-container">
      <div className="signup-form-wrapper">
        <h2 className="signup-form-title">Sign Up</h2>
        <p className="signup-form-subtitle">
          Please fill in this form to create an account!
        </p>
        <form className="signup-form" onSubmit={handleSubmit}>
          <div className="signup-form-input-group">
            <input
              name="firstName"
              type="text"
              required
              className="signup-form-input"
              placeholder="First Name"
              value={formData.firstName}
              onChange={handleChange}
            />
            <input
              name="lastName"
              type="text"
              required
              className="signup-form-input"
              placeholder="Last Name"
              value={formData.lastName}
              onChange={handleChange}
            />
            <input
              name="email"
              type="email"
              autoComplete="email"
              required
              className="signup-form-input"
              placeholder="Email address"
              value={formData.email}
              onChange={handleChange}
            />
            <input
              name="password"
              type="password"
              autoComplete="new-password"
              required
              className="signup-form-input"
              placeholder="Password"
              value={formData.password}
              onChange={handleChange}
            />
            <input
              name="confirmPassword"
              type="password"
              autoComplete="new-password"
              required
              className="signup-form-input"
              placeholder="Confirm Password"
              value={formData.confirmPassword}
              onChange={handleChange}
            />
            {passwordError && <p className="signup-form-error">{passwordError}</p>}
          </div>

          <div className="signup-form-checkbox-container">
            <input
              id="terms"
              name="terms"
              type="checkbox"
              className="signup-form-checkbox"
              checked={agreeTerms}
              onChange={() => setAgreeTerms(!agreeTerms)}
            />
            <label htmlFor="terms" className="signup-form-checkbox-label">
              I agree to the{" "}
              <Link to="#" className="text-indigo-600 hover:text-indigo-500">
                Terms of Service
              </Link>
              {" "}and{" "}
              <Link to="#" className="text-indigo-600 hover:text-indigo-500">
                Privacy Policy
              </Link>
            </label>
          </div>

          <button
            type="submit"
            className="signup-form-submit-button"
            disabled={!isFormValid}
          >
            Sign Up
          </button>
        </form>
        <div className="signup-form-login-link">
          <Link to="/login">
            Already have an account? Log in
          </Link>
        </div>
      </div>
    </div>
  );
}

export default SignUpForm;
